    private void btnOkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnOkActionPerformed
        //Object szoveg = cmdMeret.getSelectedItem().toString();
        String szoveg = (String) cmdMeret.getSelectedItem();
        int index = cmdMeret.getSelectedIndex();
        
        //System.out.printf("papír: %s, [%d]", szoveg, index);
        if(index > 0){
            System.exit(0);
        }else{
            JOptionPane.showMessageDialog(rootPane, "Nincs papír kiválasztva");
        }
    }//GEN-LAST:event_btnOkActionPerformed

    private void lblGoogleMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblGoogleMouseClicked
        try {
            //Desktop.getDesktop().browse(new URI("http://google.com"));
            Desktop.getDesktop().browse(URI.create("http://google.com"));
        } catch (IOException ex) {
            Logger.getLogger(Oldalbeallitas.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_lblGoogleMouseClicked

