    private void csere(){
        String felso, also, jobb, bal;
        jobb = txtJobb.getText();
        bal = txtBal.getText();
        also = txtAlso.getText();
        felso = txtFelso.getText();
        
        txtBal.setText(felso);
        txtFelso.setText(bal);
        
        txtJobb.setText(also);
        txtAlso.setText(jobb);
    }
    
